import React, { useState } from 'react';
import { Card,Checkbox,BackTop } from 'antd';
import { Image } from 'antd';
import { Space, Typography, Row, Col,Divider } from 'antd';
import axios from 'axios';
import GlobalData from './globalData';
import 'antd/dist/antd.css';
import Uploads from './uploads';
import { Affix, Button } from 'antd'
import Create from './create';
import UploadVideo from'./uploadVideo';

// var items=[]


const upstyle = {
  height: 40,
  width: 40,
  lineHeight: '40px',
  borderRadius: 4,
  backgroundColor: '#1088e9',
  color: '#fff',
  textAlign: 'center',
  fontSize: 14,
};

class ShowImgs extends React.Component {
  constructor(props){
    super(props);
    this.state={
      items:[],
      chosenimg:[],
    }
    this.ChooseImg= this.ChooseImg.bind(this);
    // this.=;
  }
  componentDidMount = () => {
    console.log("before:");
    console.log(items);
    var items=[];
    items.push(
      <Col span={6}>
       <Uploads/>
        </Col>)
    items.push(
      <Col span={6}>
       <UploadVideo/>
        </Col>)
    console.log("after:");
    console.log(items);
    axios({
      url:'http://127.0.0.1:5000/myimg',
      data:{
          "user_id":GlobalData.userid
      },
      method:'POST'
      }).then(
          res => {
            // console.log(res);
            
            // console.log(res.data.data[0].id);
            // console.log(res.data.imgList);
            for (let i = 0; i < res.data.imgList.length; i++) {       
              items.push(
                <Col span={6}>
                  {/* <Card  bordered={false}> */}
                  <Image
                    width={200}
                    height={200}
                    title="123"
                    // src="error"
                    fallback="data:image/png;base64,"
                    src={"upload/img/"+res.data.imgList[i].url}
                  />
                  <br></br>
                  <Checkbox id={res.data.imgList[i].id} onChange={this.ChooseImg}>{res.data.imgList[i].name}</Checkbox>
                  {/* </Card> */}
                </Col>
                );
          }
          console.log("afterpush:");
          console.log(items)
            // console.log(res.data.enterprise_info)
            // this.setState({en_list:res.data.enterprise_info})
            // this.setState({mat_list:res.data.data})
          this.setState({items:items});
          }
          
      ).catch(
          err => console.error(err)
    )
  
  }
  ChooseImg(e) {
    if(e.target.checked){
      console.log(`checked = ${e.target.id}`);
      var tmp=this.state.chosenimg;
      console.log(tmp);
      tmp.push(e.target.id);
      this.setState({chosenimg:tmp});
      console.log(tmp);
      console.log(this.state);
    }else{
      console.log(`unchecked = ${e.target.id}`);
      var tmp=this.state.chosenimg;
      var del=[];
      del.length=0;
      // console.log("del is")
      
      // console.log(del);
      // this.setState({chosenimg:[]});
      // console.log(this.state);
      for(let i=0;i<tmp.length;i++){
        // console.log("tmp[i]="+tmp[i])
        // console.log(e.target.id)
          if(tmp[i]!=e.target.id){
            // console.log("push");
            del.push(tmp[i]);
            // console.log(del);
          }
            
      }
      console.log(del);
      this.setState({chosenimg:del});
      console.log(this.state);
    }
  }

  render(){
    return (
      <>
          <Row justify="cneter" gutter={[8, 16]}>
            {this.state.items}
          </Row>
        
        
        <Create chosenimg={this.state.chosenimg} />
        <BackTop>
          <div style={upstyle}>UP</div>
        </BackTop>
      </>
    )  
  }
  
};


export default ShowImgs;